    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        var scrollTextLayout = findViewById<ScrollTextLayout>(R.id.scrolltext)
        var scrollTextLayoutParams = ScrollTextLayoutParams()
        scrollTextLayoutParams.content = content
        scrollTextLayoutParams.textSize = 20
        scrollTextLayoutParams.textColor = Color.RED
        scrollTextLayoutParams.scrollDirect = ScrollTextLayoutParams.SCROLL_RIGHT_TO_LEFT
        scrollTextLayoutParams.scrollSpeed = ScrollTextLayoutParams.SCROLL_SPEED_FAST
        scrollTextLayout.prepareScroll(scrollTextLayoutParams,object :ScrollTextLayoutListener{
            override fun onViewShowListener() {
                scrollTextLayout.startScroll()
            }

            override fun onStopScrollListener() {

            }

            override fun onStartScrollListener() {

            }

        })
    }

}